import { useParams } from 'react-router-dom';

function Recipe() {
  let { recipeID } = useParams();

  try {
    var recipe = require(`../../../data/recipes/${recipeID}.json`);
  } catch (e) {
    return <>Recipe: Not found</>;
  }

  const ingredients = [];

  recipe.ingredients.forEach((ingredient) =>
    ingredients.push(require(`../../../data/recipes/${ingredient.id}.json`))
  );

  return (
    <div className="recipe">
      Recipe: {recipe.name}
      Ingredients: {ingredients.forEach((ingredient) => ingredient.name)}
    </div>
  );
}

export default Recipe;
