import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Ingredient from '../Ingredient/Ingredient';

function Recipe() {
  const [recipeJSON, setRecipeJSON] = useState(null);
  const [ingredients, setIngredients] = useState([]);

  let { recipeID } = useParams();

  useEffect(() => {
    try {
      var json = require(`../../../data/recipes/${recipeID}.json`);
      setRecipeJSON(json);
    } catch (e) {
      setRecipeJSON(null);
    }
  }, [recipeID]);

  useEffect(() => {
    if (recipeJSON == null) {
      return;
    }

    console.log('ingredients are here:');
    console.log(recipeJSON.ingredients);
    const intermediateIngredients = [];
    recipeJSON.ingredients.forEach((ingredient) => {
      var ingredientJSON = require(`../../../data/ingredients/${ingredient.id}.json`);
      intermediateIngredients.push(ingredientJSON);
    });

    setIngredients((ingredients) => [...ingredients, intermediateIngredients]);
  }, [recipeJSON]);

  if (recipeJSON == null) {
    return <>Recipe: Not Found</>;
  }

  return (
    <div className="recipe">
      Recipe: {recipeJSON.name}
      Ingredients:
      {ingredients.forEach((ingredient) => {
        console.log('Ingredient:');
        console.log(ingredient);
        return <Ingredient ingredient={ingredient}>asdf</Ingredient>;
      })}
    </div>
  );
}

export default Recipe;
