import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Ingredient from '../Ingredient/Ingredient';

function Recipe() {
  const [recipe, setRecipe] = useState('');
  const [ingredients, setIngredients] = useState([]);

  let { recipeID } = useParams();

  fail = false;
  try {
    var recipeJSON = require(`../../../data/recipes/${recipeID}.json`);
  } catch (e) {
    fail = true;
  }

  if (fail) {
    return <>Recipe: Not found</>;
  }

  setRecipe(recipeJSON);
  useEffect(() => {
    console.log('ingredients are here:');
    console.log(recipeJSON.ingredients);
    recipeJSON.ingredients.forEach((ingredient) => {
      var ingredientJSON = require(`../../../data/ingredients/${ingredient.id}.json`);

      setIngredients((ingredients) => {
        console.log('Pushing: ');
        console.log(ingredientJSON);
        console.log('\n\n');
        ingredients.push(ingredientJSON);
        console.log(ingredients);
        return ingredients;
      });
    });
  }, [recipeID]);

  return (
    <div className="recipe">
      Recipe: {recipe.name}
      Ingredients:
      {ingredients.forEach((ingredient) => (
        <Ingredient ingredient={ingredient}></Ingredient>
      ))}
    </div>
  );
}

export default Recipe;
